DROP TABLE IF EXISTS `recipe_item`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `recipe_item` (
                               `recipe_item_id` bigint NOT NULL AUTO_INCREMENT COMMENT 'ID',
                               `recipe_id` bigint NOT NULL COMMENT '食谱ID',
                               `ingredient_id` bigint NOT NULL COMMENT '食材ID',
                               `dosage` decimal(10,0) NOT NULL COMMENT '用量',
                               PRIMARY KEY (`recipe_item_id`),
                               KEY `recipe_item_ingredient_Ingredient_id_fk` (`ingredient_id`),
                               KEY `recipe_item_recipe_recipe_id_fk` (`recipe_id`),
